SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE receber_resp_envio_wau ( nr_cot_compra_p bigint, ie_status_retorno_p text, ds_retorno_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_status_retorno_p = '1') then

	update	cot_compra
	set	ie_status_envio	= 'O'
	where	nr_cot_compra	= nr_cot_compra_p;

	insert into cot_compra_log_integracao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_cot_compra,
		ie_status_envio,
		ie_tipo_integracao,
		ds_log)
	values (	nextval('cot_compra_log_integracao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_cot_compra_p,
		'OA',
		'C',
		Wheb_mensagem_pck.get_Texto(306726)); /*'A alteração da cotação foi integrada corretamente.');*/
	update	cot_compra_fila_transm
	set	ie_evento = 'R'
	where	nr_cot_compra = nr_cot_compra_p
	and	ie_evento = 'EA';
else
	update	cot_compra
	set	ie_status_envio	= 'E'
	where	nr_cot_compra	= nr_cot_compra_p;

	insert into cot_compra_log_integracao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_cot_compra,
		ie_status_envio,
		ie_tipo_integracao,
		ds_log)
	values (	nextval('cot_compra_log_integracao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_cot_compra_p,
		'EA',
		'C',
		Wheb_mensagem_pck.get_Texto(306728, 'DS_RETORNO_P='|| DS_RETORNO_P));
		/*A alteração da cotação foi enviada, porém não foi integrada porque retornou o seguinte erro:
		#@DS_RETORNO_P#@*/
	update	cot_compra_fila_transm
	set	ie_evento = 'R'
	where	nr_cot_compra = nr_cot_compra_p
	and	ie_evento = 'EA';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
