SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE receber_resp_envio_wmg ( cd_cnpj_p text, ie_status_retorno_p text, ds_retorno_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_status_retorno_p = '1') then

	update	pessoa_juridica
	set	ie_status_envio	= 'O'
	where	cd_cgc	= cd_cnpj_p;

	insert into cot_compra_log_integracao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_cnpj,
		ie_status_envio,
		ie_tipo_integracao,
		ds_log)
	values (	nextval('cot_compra_log_integracao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_cnpj_p,
		'OJ',
		'P',
		Wheb_mensagem_pck.get_Texto(306739)); /*'A pessoa jurídica foi integrada corretamente.'*/
	update	pessoa_jur_fila_transm
	set	ie_evento = 'R'
	where	cd_cgc = cd_cnpj_p
	and	ie_evento = 'EN';

else

	update	pessoa_juridica
	set	ie_status_envio	= 'E'
	where	cd_cgc	= cd_cnpj_p;

	insert into cot_compra_log_integracao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_cnpj,
		ie_status_envio,
		ie_tipo_integracao,
		ds_log)
	values (	nextval('cot_compra_log_integracao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_cnpj_p,
		'EJ',
		'P',
		Wheb_mensagem_pck.get_Texto(306740, 'DS_RETORNO_P='|| DS_RETORNO_P));
		/*A pessoa jurídica foi enviada, porém não foi integrada porque retornou o seguinte erro: #@DS_RETORNO_P#@*/

	update	pessoa_jur_fila_transm
	set	ie_evento = 'R'
	where	cd_cgc = cd_cnpj_p
	and	ie_evento = 'EN';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
