CREATE OR REPLACE PROCEDURE receber_solic_compra ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
update	solic_compra 
set	dt_recebimento		= clock_timestamp(), 
	dt_atualizacao		= clock_timestamp(), 
	nm_usuario		= nm_usuario_p, 
	nm_usuario_receb		= nm_usuario_p 
where	nr_solic_compra		= nr_solic_compra_p;
 
CALL gerar_historico_solic_compra( 
	nr_solic_compra_p, 
	Wheb_mensagem_pck.get_Texto(306741), /*'Recebimento da solicitação de compras',*/
 
	Wheb_mensagem_pck.get_Texto(306743, 'NM_USUARIO_P='|| NM_USUARIO_P), /*'Foi gerado o recebimento desta solicitação de compras pelo usuário ' || nm_usuario_p || ' através da opção Receber solicitação de compras.',*/ 
	'RSC', 
	nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
