CREATE OR REPLACE PROCEDURE receber_transferencia_pertence ( nr_sequencia_p bigint, qt_item_recebimento_p bigint, nr_seq_registro_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint) AS $body$
DECLARE


			
			
ds_observacao_w		   varchar(2000);		
ds_especificacao_w	   varchar(2000);		
nr_seq_pertence_w	   bigint;
dt_exame_w			   timestamp;
nr_sequencia_w		   bigint;
cd_setor_atendimento_w bigint;
nr_seq_armario_w	   bigint;

BEGIN

select	nr_seq_pertence,
	ds_especificacao,
	ds_observacao,
	dt_exame
into STRICT	nr_seq_pertence_w,
	ds_especificacao_w,
	ds_observacao_w,
	dt_exame_w
from	pertence_paciente_item
where	nr_sequencia = nr_sequencia_p;


if (qt_item_recebimento_p > 0) then
	begin
	
	select  nextval('pertence_paciente_item_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into	pertence_paciente_item(
				nr_sequencia,
				dt_Atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_liberacao,
				nr_seq_pertence,
				ds_especificacao,
				ds_observacao,
				dt_exame,
				qt_item,
				nr_seq_pertence_paciente)
			values (
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_pertence_w,
				ds_especificacao_w,
				ds_observacao_w,
				dt_exame_w,
				qt_item_recebimento_p,
				nr_seq_registro_p);
	
	insert into	pertence_pac_item_evento(
				nr_sequencia,
				nr_seq_pert_pac,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_tipo_evento,
				qt_transacao,
				cd_setor_atendimento)
			values (
				nextval('pertence_pac_item_evento_seq'),
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'R',
				qt_item_recebimento_p,
				cd_setor_atendimento_p);
	end;
	
	update 	pertence_pac_item_evento
	set	   	nm_usuario_receb = nm_usuario_p
	where  	nr_seq_pert_pac = nr_sequencia_p
	and	   	coalesce(nm_usuario_receb::text, '') = ''
	and	  	cd_tipo_evento = 'T';
	
	select max(cd_setor_atendimento), max(nr_seq_armario)
	into STRICT cd_setor_atendimento_w, nr_seq_armario_w
	from pertence_pac_item_evento
	where  	nr_seq_pert_pac = nr_sequencia_p
	and	   	(nm_usuario_receb IS NOT NULL AND nm_usuario_receb::text <> '')
	and	  	cd_tipo_evento = 'T';
	
	if((cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') or (nr_seq_armario_w IS NOT NULL AND nr_seq_armario_w::text <> '')) then
		update pertence_paciente
		set nr_seq_armario = nr_seq_armario_w,
			cd_setor_atendimento = cd_setor_atendimento_w
		where nr_sequencia = nr_seq_registro_p
		and (coalesce(cd_setor_atendimento::text, '') = '' or coalesce(nr_seq_armario::text, '') = '');
	end if;
	
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
