CREATE OR REPLACE PROCEDURE recebe_confirmacao_envio_cot ( nr_cot_compra_p bigint, ie_status_p bigint, nr_documento_externo_p text, ds_retorno_integracao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_enter_w	varchar(10) := chr(13) || chr(10);
nr_cot_compra_w	bigint;
nm_computador_w			varchar(80);


BEGIN
select	coalesce(max(nr_cot_compra),0)
into STRICT	nr_cot_compra_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (nr_cot_compra_w > 0) then
	begin

	if (ie_status_p < 0) then
		CALL gerar_historico_cotacao(
			nr_cot_compra_p,
			WHEB_MENSAGEM_PCK.get_texto(306412),
			substr(WHEB_MENSAGEM_PCK.get_texto(306413,'NR_DOCUMENTO_EXTERNO_P='||substr(nr_documento_externo_p,1,255)||
				';DS_RETORNO_INTEGRACAO_P='||substr(ds_retorno_integracao_p,1,3600)),1,4000),
			'S',
			nm_usuario_p);
	elsif (ie_status_p = 1) then
		update	cot_compra
		set	nr_documento_externo	= nr_documento_externo_p
		where	nr_cot_compra		= nr_cot_compra_p;

		CALL gerar_historico_cotacao(
			nr_cot_compra_p,
			WHEB_MENSAGEM_PCK.get_texto(306412),
			substr(WHEB_MENSAGEM_PCK.get_texto(306414,'DS_RETORNO_INTEGRACAO_P='||substr(ds_retorno_integracao_p,1,3950)),1,4000),
			'S',
			nm_usuario_p);

	elsif (ie_status_p = 0) then
		CALL gerar_historico_cotacao(
			nr_cot_compra_p,
			WHEB_MENSAGEM_PCK.get_texto(306412),
			substr(WHEB_MENSAGEM_PCK.get_texto(306415,'DS_RETORNO_INTEGRACAO_P='||substr(ds_retorno_integracao_p,1,3950)),1,4000),
			'S',
			nm_usuario_p);
	end if;

	commit;
	end;
end if;

end;
$body$
