SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recebe_nota_rps_nfe ( cd_estabelecimento_p bigint, nr_nota_fiscal_p bigint, cd_serie_nf_p text, cd_cpf_cnpj_prest_p text, dt_emissao_p text, nr_nfe_imp_p text, ds_observacao_p text, nr_seq_lote_p bigint, ie_situacao_p text default '') AS $body$
DECLARE


nr_sequencia_w	bigint;
cd_serie_nr_w	varchar(5);
dt_emissao_w	timestamp;


BEGIN

/*conversao de formato de data*/

begin

if (dt_emissao_p IS NOT NULL AND dt_emissao_p::text <> '') then
  dt_emissao_w	:= dt_emissao_p;
end if;

exception when others then
	begin
	dt_emissao_w		:= to_date(dt_emissao_p,'yyyy-mm-dd');
	exception when others then
		begin
		dt_emissao_w	:= to_date(dt_emissao_p,'dd-mm-yyyy');
		exception when others then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266357,'DT_EMISSAO=' || dt_emissao_p);
			--'Nao foi possivel converter a data de emissao enviada no arquivo: ' + dt_emissao_p);
		end;
	end;
end;

begin

select	coalesce(max(replace(cd_serie_nf_p,' ','')),'X')
into STRICT	cd_serie_nr_w
;

if (dt_emissao_w IS NOT NULL AND dt_emissao_w::text <> '') then
	select	coalesce(max(nr_sequencia),0)
  into STRICT	nr_sequencia_w
  from	nota_fiscal
  where	cd_estabelecimento	= cd_estabelecimento_p
  and	nr_nota_fiscal		= to_char(nr_nota_fiscal_p)
  and (cd_serie_nf		= cd_serie_nf_p or cd_serie_nr_w = 'X')
  and	cd_cgc_emitente		= cd_cpf_cnpj_prest_p
  and (dt_emissao >= trunc(dt_emissao_w, 'dd') and dt_emissao < trunc(dt_emissao_w + 1, 'dd'))
  and	coalesce(nr_nfe_imp,0)	<> 	nr_nfe_imp_p;
else
	select	coalesce(max(nr_sequencia),0)
  into STRICT	nr_sequencia_w
  from	nota_fiscal
  where	cd_estabelecimento	= cd_estabelecimento_p
  and	nr_nota_fiscal		= to_char(nr_nota_fiscal_p)
  and (cd_serie_nf		= cd_serie_nf_p or cd_serie_nr_w = 'X')
  and	cd_cgc_emitente		= cd_cpf_cnpj_prest_p
  and	coalesce(nr_nfe_imp,0)	<> 	nr_nfe_imp_p;
end if;

if (nr_sequencia_w > 0) then

	if (ie_situacao_p = 'C') then
		update	nota_fiscal
		set	nr_nfe_imp 		= nr_nfe_imp_p,
			nr_seq_importada	= nr_seq_lote_p,
			ie_status_envio		= ie_situacao_p
		where	nr_sequencia 		= nr_sequencia_w;
	else
		update	nota_fiscal
		set	nr_nfe_imp 		= nr_nfe_imp_p,
			nr_seq_importada	= nr_seq_lote_p
		where	nr_sequencia 		= nr_sequencia_w;
	end if;
	update	nota_fiscal_item
	set	nr_nfe_imp 	= nr_nfe_imp_p
	where	nr_sequencia 	= nr_sequencia_w;
	
	insert into nota_fiscal_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_nota,
		cd_evento,
		ds_historico)
	values (	nextval('nota_fiscal_hist_seq'),
		clock_timestamp(),
		'Tasy',
		clock_timestamp(),
		'Tasy',
		nr_sequencia_w,
		'27',
		--substr('NF-e importada: ' || nr_nfe_imp_p || ' ' || ds_observacao_p,1,255));
		substr(wheb_mensagem_pck.get_texto(311869, 'NR_NFE_IMP=' || nr_nfe_imp_p) || ' ' || ds_observacao_p,1,255));
end if;


exception when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266358,'NR_NOTA_FISCAL=' || nr_nota_fiscal_p || ';' ||
							'CD_ESTABELECIMENTO=' || cd_estabelecimento_p || ';' ||
							'CD_SERIE_NF=' || cd_serie_nf_p || ';' ||
							'DT_EMISSAO=' || dt_emissao_w || ';' ||
							'CD_CPF_CNPJ=' || cd_cpf_cnpj_prest_p || ';' ||
							'NR_NFE_IMP=' || nr_nfe_imp_p);
	--'Erro na atualizacao da nf: ' 	|| nr_nota_fiscal_p		|| chr(13) || chr(10) ||

	--'Estab: '		|| cd_estabelecimento_p 	|| chr(13) || chr(10) ||

	--'Serie: ' 		|| cd_serie_nf_p		|| chr(13) || chr(10) ||

	--'Emissao: ' 		|| dt_emissao_w		|| chr(13) || chr(10) ||

	--'CNPJ: '		|| cd_cpf_cnpj_prest_p 	|| chr(13) || chr(10) ||

	--'Nr NF-e: '		|| nr_nfe_imp_p);
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE recebe_nota_rps_nfe ( cd_estabelecimento_p bigint, nr_nota_fiscal_p bigint, cd_serie_nf_p text, cd_cpf_cnpj_prest_p text, dt_emissao_p text, nr_nfe_imp_p text, ds_observacao_p text, nr_seq_lote_p bigint, ie_situacao_p text default '') FROM PUBLIC;

