SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recebimento_fisico_devolucao ( nr_devolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_existe_w	integer;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	devolucao_material_pac
where	nr_devolucao = nr_devolucao_p;
if (qt_existe_w = 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(267238, 'nr_devolucao='||nr_devolucao_p);
	--'Não encontrada a devolução de número ' || nr_devolucao_p);
else
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	devolucao_material_pac
	where	nr_devolucao = nr_devolucao_p
	and	coalesce(dt_entrega_fisica::text, '') = '';
	if (qt_existe_w = 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(267239);
		--'Esta devolução já tem o registro de recebimento.'
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	devolucao_material_pac
	where	nr_devolucao = nr_devolucao_p
	and	(dt_liberacao_baixa IS NOT NULL AND dt_liberacao_baixa::text <> '');
	if (qt_existe_w = 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(267240);
		--'Esta devolução ainda não está liberada.'
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	devolucao_material_pac
	where	nr_devolucao = nr_devolucao_p
	and	coalesce(dt_baixa_total::text, '') = '';
	if (qt_existe_w = 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(267241);
		--'Esta devolução já tem data de baixa.
	end if;


	update	devolucao_material_pac
	set	dt_entrega_fisica = clock_timestamp(),
		nm_usuario_entrega = nm_usuario_p
	where	nr_devolucao = nr_devolucao_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
