CREATE OR REPLACE PROCEDURE recent_patients_found_handler (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ie_trigger_p text) AS $body$
DECLARE


ie_exists	varchar(1) := 'N';

BEGIN

/*
either nr_atendimento_p or cd_pessoa_fisica_p should have values, never both
*/
if (ie_trigger_p = 'N') then

	begin
		select	'S'
		into STRICT	ie_exists
		from	recent_patients_found
		where (nr_atendimento = coalesce(nr_atendimento_p,0)
		or		cd_pessoa_fisica = coalesce(cd_pessoa_fisica_p,'XXXXX'))
		and 	nm_usuario = nm_usuario_p  LIMIT 1;

		update	recent_patients_found
		set		dt_atualizacao = clock_timestamp()
		where (nr_atendimento = coalesce(nr_atendimento_p,0)
		or		cd_pessoa_fisica = coalesce(cd_pessoa_fisica_p,'XXXXX'))
		and 	nm_usuario = nm_usuario_p;
		commit;
	exception
	when others then
	insert into recent_patients_found(
		dt_atualizacao,
		nm_usuario,
		nr_atendimento,
		cd_pessoa_fisica
	) values (
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento_p,
		cd_pessoa_fisica_p
	);
	commit;
	end;
else
	insert into recent_patients_found(
		dt_atualizacao,
		nm_usuario,
		nr_atendimento,
		cd_pessoa_fisica
	) values (
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento_p,
		cd_pessoa_fisica_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
