SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recep_atual_canc_autorizacao ( nm_usuario_p text, nr_seq_agenda_p bigint) AS $body$
DECLARE


nr_seq_autorizacao_w	bigint;
nr_seq_estagio_w	bigint;
qt_cancelado_w		bigint;


BEGIN

select	max(nr_sequencia) nr_seq_autorizacao
into STRICT	nr_seq_autorizacao_w
from  	autorizacao_convenio
where  	nr_seq_agenda  = nr_seq_agenda_p;

if (coalesce(nr_seq_autorizacao_w,0) > 0) then


	select	max(nr_sequencia) nr_seq_estagio
	into STRICT	nr_seq_estagio_w
	from  	estagio_autorizacao
	where 	ie_situacao = 'A'
	and   	ie_interno  = '70'
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

	select	count(*)
	into STRICT	qt_cancelado_w
	from	autorizacao_convenio a
	where	nr_sequencia = nr_seq_autorizacao_w
	and 	nr_seq_estagio = nr_seq_estagio_w;

	if (coalesce(qt_cancelado_w,0) > 0) then

		CALL atualizar_autorizacao_convenio(nr_seq_autorizacao_w,nm_usuario_p,nr_seq_estagio_w,'N','N','S');

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
