SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recep_cons_prescr_atend_conv (nr_atendimento_p bigint, ie_obriga_conv_p INOUT text) AS $body$
DECLARE


ie_obriga_conv_w		varchar(01) := 'N';
nr_prescr_w			bigint := 0;
nr_proc_prescr_w		bigint := 0;
nr_atend_cat_w			bigint := 0;


BEGIN

select	count(*)
into STRICT	nr_atend_cat_w
from	atend_categoria_convenio
where	nr_atendimento	= nr_atendimento_p;

if (nr_atend_cat_w = 0) then
	begin

	select	count(*)
	into STRICT	nr_prescr_w
	from	prescr_medica
	where	nr_atendimento	= nr_atendimento_p;

	select	count(*)
	into STRICT	nr_proc_prescr_w
	from	prescr_medica a,
		prescr_procedimento b
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p;

	if (nr_proc_prescr_w > 0) or (nr_prescr_w > 0) then
		ie_obriga_conv_w	:= 'S';
	end if;

	end;
end if;

ie_obriga_conv_p	:= ie_obriga_conv_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
