CREATE OR REPLACE PROCEDURE recep_liberar_prescricoes ( nr_atendimento_p bigint, ie_tipo_pessoa_p text, cd_perfil_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_prescricao_w	bigint;

C01 CURSOR FOR 
	SELECT	coalesce(nr_prescricao,0) 
	from	prescr_medica 
	where	nr_atendimento = nr_atendimento_p 
	and		coalesce(dt_liberacao::text, '') = '' 
	and		coalesce(dt_suspensao::text, '') = '';									
									 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (nr_prescricao_w > 0) then 
 
		ds_erro_p := liberar_prescricao(nr_prescricao_w, nr_atendimento_p, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, 'N', ds_erro_p);
	 
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
