CREATE OR REPLACE PROCEDURE recep_obter_presc_atend_exame ( nr_seq_ficha_agenda_p bigint, nr_seq_agenda_p bigint, nr_atendimento_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE



nr_seq_ficha_agenda_w	bigint;
nr_seq_agenda_w 	bigint;
nr_atendimento_w	bigint;
nr_prescricao_w		bigint;

cd_convenio_w 		bigint;
cd_categoria_w		bigint;

nr_seq_interno_w 	bigint;


BEGIN

nr_atendimento_w := nr_atendimento_p;
nr_prescricao_w := -1;

if ((nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and nr_seq_agenda_p > 0) then
	SELECT	max(cd_convenio),
		max(cd_categoria)
	INTO STRICT	cd_convenio_w,
		cd_categoria_w
	FROM	agenda_paciente
	WHERE	nr_sequencia = nr_seq_agenda_p;

	if ((cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') and cd_convenio_w > 0) then
		SELECT	nr_seq_interno
		INTO STRICT    nr_seq_interno_w
		FROM	atend_categoria_convenio
		WHERE	nr_atendimento = nr_atendimento_w
		AND	cd_convenio = cd_convenio_w
		AND	cd_categoria = cd_categoria_w;

		if ((nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') and nr_seq_interno_w > 0) then
			SELECT nr_prescricao
			INTO STRICT nr_prescricao_w
			FROM prescr_medica
			WHERE nr_seq_atecaco = nr_seq_interno_w;

		end if;

	end if;

else
        SELECT	MAX(cd_convenio),
		MAX(cd_categoria)
	INTO STRICT	cd_convenio_w,
		cd_categoria_w
	FROM	AGEINT_EXAME_LAB_CONV_ITEM
	WHERE	nr_seq_lab_item = nr_seq_ficha_agenda_p;

	if ((cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') and cd_convenio_w > 0) then
		SELECT	nr_seq_interno
		INTO STRICT    nr_seq_interno_w
		FROM	atend_categoria_convenio
		WHERE	nr_atendimento = nr_atendimento_w
		AND	cd_convenio = cd_convenio_w
		AND	cd_categoria = cd_categoria_w;

		if ((nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') and nr_seq_interno_w > 0) then
			SELECT nr_prescricao
			INTO STRICT nr_prescricao_w
			FROM prescr_medica
			WHERE nr_seq_atecaco = nr_seq_interno_w;

		end if;
	end if;



end if;

nr_prescricao_p := nr_prescricao_w;


end;
