CREATE OR REPLACE PROCEDURE recep_obter_proced_agenda ( nr_seq_ficha_agenda_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, qt_procedimento_p INOUT bigint, cd_procedimento_p INOUT bigint, nr_seq_proc_interno_p INOUT bigint, cd_medico_exec_p INOUT text, ie_lado_p INOUT text, ds_senha_p INOUT text, cd_setor_atendimento_p INOUT bigint, nr_doc_convenio_p INOUT text, qt_proced_prescr_p INOUT bigint, cd_equipamento_p INOUT text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
 
nr_seq_agenda_w 	bigint;
nr_seq_age_proc_w 	bigint;	
qt_proc_w		bigint;	
nr_atendimento_w	bigint;
			

BEGIN 
 
qt_proc_w := obter_param_usuario(914, 8, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, qt_proc_w);
 
if (coalesce(nr_seq_ficha_agenda_p,0) > 0) then 
	 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from 	recep_ficha_pre 
	where 	nr_sequencia = nr_seq_ficha_agenda_p;
	 
	select	max(coalesce(nr_seq_agenda,0)), 
		max(coalesce(nr_seq_age_proc,0)) 
	into STRICT	nr_seq_agenda_w, 
		nr_seq_age_proc_w 
	from	recep_ficha_pre_agenda 
	where	nr_seq_agenda = nr_seq_agenda_p;
	 
	select	max(c.cd_equipamento) 
	into STRICT	cd_equipamento_p 
	from	recep_ficha_pre_agenda a, 
			agenda_paciente b, 
			agenda c 
	where	a.nr_seq_agenda = b.nr_sequencia 
	and		b.cd_agenda = c.cd_agenda 
	and		a.nr_seq_agenda = nr_seq_agenda_p 
	and		a.nr_sequencia = nr_seq_ficha_agenda_p;
 
	if (coalesce(nr_seq_age_proc_w,0) > 0) then 
		 
		select	coalesce(cd_procedimento,0), 
			qt_procedimento, 
			nr_seq_proc_interno, 
			cd_medico, 
			ie_lado 
		into STRICT	cd_procedimento_p, 
			qt_procedimento_p, 
			nr_seq_proc_interno_p, 
			cd_medico_exec_p, 
			ie_lado_p 
		from	agenda_paciente_proc 
		where	nr_seq_agenda = nr_seq_age_proc_w 
		and	nr_sequencia = nr_seq_agenda_w;
		 
		if (qt_proc_w > 1) and (ie_lado_p = 'A') then 
			qt_procedimento_p := qt_proc_w;
		end if;
	 
	elsif (coalesce(nr_seq_agenda_w,0) > 0) then 
		 
		select	coalesce(cd_procedimento,0), 
			1, 
			nr_seq_proc_interno, 
			substr(cd_medico_exec,1,10), 
			ie_lado 
		into STRICT	cd_procedimento_p, 
			qt_procedimento_p, 
			nr_seq_proc_interno_p, 
			cd_medico_exec_p, 
			ie_lado_p 
		from	agenda_paciente 
		where	nr_sequencia = nr_seq_agenda_w;
		 
		if (qt_proc_w > 1) and (ie_lado_p = 'A') then 
			qt_procedimento_p := qt_proc_w;
		end if;
		 
	end if;
	 
	select	max(nr_doc_convenio), 
		max(ds_senha), 
		max(cd_setor_atendimento), 
		max(cd_convenio), 
		max(cd_categoria) 
	into STRICT	nr_doc_convenio_p, 
		ds_senha_p, 
		cd_setor_atendimento_p, 
		cd_convenio_p, 
		cd_categoria_p 
	from	agenda_paciente 
	where	nr_sequencia = nr_seq_agenda_w;
	 
	select	count(*) 
	into STRICT	qt_proced_prescr_p 
	from	prescr_procedimento 
	where 	nr_seq_agenda = nr_seq_agenda_w 
	and	cd_procedimento = cd_procedimento_p;
	 
	select	max(b.nr_prescricao) 
	into STRICT	nr_prescricao_p 
	from	atend_categoria_convenio a, 
		prescr_medica b 
	where	a.nr_atendimento = nr_atendimento_w 
	and	a.nr_seq_interno = b.nr_seq_atecaco 
	and	a.cd_convenio = cd_convenio_p 
	and	a.cd_categoria = cd_categoria_p;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE recep_obter_proced_agenda ( nr_seq_ficha_agenda_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, qt_procedimento_p INOUT bigint, cd_procedimento_p INOUT bigint, nr_seq_proc_interno_p INOUT bigint, cd_medico_exec_p INOUT text, ie_lado_p INOUT text, ds_senha_p INOUT text, cd_setor_atendimento_p INOUT bigint, nr_doc_convenio_p INOUT text, qt_proced_prescr_p INOUT bigint, cd_equipamento_p INOUT text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, nr_prescricao_p INOUT bigint) FROM PUBLIC;

