CREATE OR REPLACE PROCEDURE recep_pac_recepcionado ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


--ie_opcao_p
--'N' aguardando recepcao
--'S' sendo recepcionado
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

begin

	update 	recep_ficha_pre
	set	ie_pac_recepcao = ie_opcao_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario_recep = CASE WHEN ie_opcao_p='S' THEN  nm_usuario_p  ELSE null END
	where	nr_sequencia = nr_sequencia_p
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_status = 1;

end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
