CREATE OR REPLACE PROCEDURE reconst_before_delete_js ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_reg_alteracao_farm_w	varchar(1);
cd_material_w	integer;
qt_dose_w	bigint;
qt_solucao_w	double precision;
cd_unidade_medida_dose_w varchar(30);


BEGIN
ie_reg_alteracao_farm_w := obter_param_usuario(7010, 48, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_reg_alteracao_farm_w);

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	select  max(cd_material),
		max(qt_dose),
		max(qt_solucao),
		max(cd_unidade_medida_dose)
	into STRICT	cd_material_w,
		qt_dose_w,
		qt_solucao_w,
		cd_unidade_medida_dose_w
	from    prescr_material
	where   nr_prescricao           = nr_prescricao_p
	and     nr_sequencia_diluicao   = nr_sequencia_p
	and     ie_agrupador            = 9;

	if (ie_reg_alteracao_farm_w = 'S') then
		CALL gerar_historico_dil_red_rec('N', 3, 9, nr_prescricao_p, nr_sequencia_p, cd_material_w, 0, qt_dose_w, 0, qt_solucao_w, 0, cd_unidade_medida_dose_w, '',
		cd_estabelecimento_p, nm_usuario_p);
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
