CREATE OR REPLACE PROCEDURE recursos_agenda_ha_consistir ( nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

					 
cd_equipamento_w	bigint;
nr_seq_conjunto_w	bigint;

c01 CURSOR FOR					 
	SELECT	cd_equipamento 
	from	agenda_pac_equip 
	where	nr_seq_agenda = nr_seq_agenda_p 
	and	((coalesce(ie_status_equipamento,'I') = 'I') or (coalesce(ie_status_equipamento,'I') = 'M')) 
	and	coalesce(ie_origem_inf,'I') = 'I';

c02 CURSOR FOR					 
	SELECT	nr_seq_conjunto 
	from	agenda_pac_cme 
	where	nr_seq_agenda = nr_seq_agenda_p 
	and (coalesce(ie_status_cme,'I') = 'I') 
	and	coalesce(ie_origem_inf,'I') = 'I';
	

BEGIN 
 
delete	from consistencia_agenda_cir where nr_seq_agenda = nr_seq_agenda_p;
commit;
 
open C01;
loop 
fetch C01 into	 
	cd_equipamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL consistir_recursos_agenda(nr_seq_agenda_p,cd_equipamento_w,'',nm_usuario_p,cd_estabelecimento_p);
	end;
end loop;
close C01;
 
open C02;
loop 
fetch C02 into	 
	nr_seq_conjunto_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	CALL consistir_recursos_agenda(nr_seq_agenda_p,'',nr_seq_conjunto_w,nm_usuario_p,cd_estabelecimento_p);
	end;
end loop;
close C02;
 
end;
