CREATE OR REPLACE PROCEDURE recusa_extracao_tecido (nr_atendimento_p bigint, ds_observacao_biobanco_p text, nr_seq_motivo_biobanco_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_motivo_biobanco_w	bigint;
nr_seq_agenda_w			bigint;
nr_seq_proc_interno_w		bigint;
qt_biobanco_w			bigint;


BEGIN 
 
if (nr_seq_motivo_biobanco_p = 0) then 
	nr_seq_motivo_biobanco_w := null;
else 
	nr_seq_motivo_biobanco_w := nr_seq_motivo_biobanco_p;
end if;
 
