set	ie_assinou_termo_biobanco      = 'N', 
	ds_observacao_biobanco       = ds_observacao_biobanco_p, 
	NR_SEQ_MOTIVO_BIOBANCO  = nr_seq_motivo_biobanco_w, 
	dt_atualizacao_biobanco        = clock_timestamp(), 
	nm_usuario_biobanco	    = obter_usuario_ativo 
where	nr_atendimento = nr_atendimento_p;
 
commit;
 
end;
