SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE redefinir_aih_retorno_secr ( nr_aih_p bigint, nr_seq_laudo_p bigint, nm_usuario_p text) AS $body$
DECLARE


			
nr_aih_antiga_w		bigint;
nr_seq_aih_ant_w	bigint;
cd_pessoa_fisica_w	varchar(10);	
ie_origem_proced_w	bigint;
qt_aih_laudo_w		integer;
nr_atendimento_w	sus_laudo_paciente.nr_atendimento%type;


BEGIN

begin
select	nr_aih,
	nr_seq_aih,
	substr(obter_pessoa_atendimento(nr_atendimento, 'C'),1,10),
	ie_origem_proced,
	nr_atendimento
into STRICT	nr_aih_antiga_w,
	nr_seq_aih_ant_w,
	cd_pessoa_fisica_w,
	ie_origem_proced_w,
	nr_atendimento_w
from	sus_laudo_paciente
where	nr_seq_interno	= nr_seq_laudo_p;
exception
	when others then
	nr_aih_antiga_w		:= 0;
	nr_seq_aih_ant_w	:= 0;
end;

begin
update	sus_laudo_paciente
set	nr_aih		= nr_aih_p,
	nr_seq_aih	= 0,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_interno	=  nr_seq_laudo_p
and	nr_atendimento	= nr_atendimento_w;
exception
	when no_data_found then
		nr_aih_antiga_w	:= nr_aih_antiga_w;
	when others then
		--r.aise_application_error(-20011,'Ocorreu um erro ao atualizar os laudos desta AIH - ' || nr_aih_antiga_w);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263427,'nr_aih_antiga_w='||nr_aih_antiga_w);

end;

begin
update	sus_laudo_paciente
set	nr_aih		= nr_aih_p,
	nr_seq_aih	= 0,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_aih		= nr_aih_antiga_w
and	nr_seq_aih	= nr_seq_aih_ant_w
and	nr_seq_interno	<>  nr_seq_laudo_p
and	nr_atendimento	= nr_atendimento_w;
exception
	when no_data_found then
		nr_aih_antiga_w	:= nr_aih_antiga_w;
	when others then
		--r.aise_application_error(-20011,'Ocorreu um erro ao atualizar os laudos desta AIH - ' || nr_aih_antiga_w);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263427,'nr_aih_antiga_w='||nr_aih_antiga_w);
end;

select	count(*)
into STRICT	qt_aih_laudo_w
from	sus_aih_unif
where	nr_aih			= nr_aih_antiga_w
and	nr_atendimento		= nr_atendimento_w;

if (qt_aih_laudo_w > 0) then
	begin
	update	sus_aih_unif
	set	nr_aih			= nr_aih_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_aih			= nr_aih_antiga_w
	and	nr_atendimento		= nr_atendimento_w;	
	
	begin
	update	sus_registro_civil
	set	nr_aih		= nr_aih_p
	where	nr_aih		= nr_aih_antiga_w		
	and	nr_seq_aih	= nr_seq_aih_ant_w
	and	nr_atendimento	= nr_atendimento_w;
	exception
		when no_data_found then
			nr_aih_antiga_w	:= nr_aih_antiga_w;
		when others then
				--r.aise_application_error(-20011,'Não foi possível concluir o retorno do laudo. [Registro Civil]');
				CALL wheb_mensagem_pck.exibir_mensagem_abort(263428);
	end;
	
	begin
	update	procedimento_paciente
	set	nr_aih		= nr_aih_p
	where	nr_aih		= nr_aih_antiga_w
	and	nr_atendimento		= nr_atendimento_w;
	exception
		when no_data_found then
			nr_aih_antiga_w	:= nr_aih_antiga_w;
		when others then
			--r.aise_application_error(-20011,'Não foi possível concluir o retorno do laudo. [Procedimento]');
			CALL wheb_mensagem_pck.exibir_mensagem_abort(263429);
	end;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE redefinir_aih_retorno_secr ( nr_aih_p bigint, nr_seq_laudo_p bigint, nm_usuario_p text) FROM PUBLIC;

