CREATE OR REPLACE PROCEDURE redefinir_inventario ( nr_sequencia_p bigint, qt_definir_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_contagem_w			double precision;
qt_recontagem_w			double precision;
qt_seg_recontagem_w		double precision;
qt_terc_recontagem_w		double precision;



BEGIN

select	coalesce(max(qt_contagem),0),
	coalesce(max(qt_recontagem),0),
	coalesce(max(qt_seg_recontagem),0),
	coalesce(max(qt_terc_recontagem),0)
into STRICT	qt_contagem_w,
	qt_recontagem_w,
	qt_seg_recontagem_w,
	qt_terc_recontagem_w
from	inventario_material
where	nr_sequencia = nr_sequencia_p;

if (qt_contagem_w = 0) then
	update	inventario_material
	set	qt_contagem = qt_definir_p
	where	nr_sequencia = nr_sequencia_p;
elsif (qt_recontagem_w = 0) then
	update	inventario_material
	set	qt_recontagem = qt_definir_p
	where	nr_sequencia = nr_sequencia_p;
elsif (qt_seg_recontagem_w = 0) then
	update	inventario_material
	set	qt_seg_recontagem = qt_definir_p
	where	nr_sequencia = nr_sequencia_p;
else
	update	inventario_material
	set	qt_terc_recontagem = qt_definir_p
	where	nr_sequencia = nr_sequencia_p;
end if;

update	inventario_material
set	qt_inventario	= qt_definir_p,
	qt_diferenca	= qt_definir_p - qt_saldo,
	dt_atualizacao = clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
