CREATE OR REPLACE PROCEDURE rediluicao_material_estab_js ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_unid_med_p bigint, cd_unidade_medida_consumo_p INOUT text, qt_conversao_p INOUT bigint) AS $body$
DECLARE


qt_conversao_w			double precision;
cd_unidade_medida_consumo_w	varchar(15);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select 	substr(obter_dados_material_estab(cd_material_p, cd_estabelecimento_p,'UMS'),1,30)
	into STRICT 	cd_unidade_medida_consumo_w
	;

	select 	qt_conversao
	into STRICT	qt_conversao_w
	from 	material_conversao_unidade
	where 	cd_material = cd_material_p
	and 	cd_unidade_medida = cd_unid_med_p;
	end;
end if;
qt_conversao_p		:= qt_conversao_w;
cd_unidade_medida_consumo_p := cd_unidade_medida_consumo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
