CREATE OR REPLACE PROCEDURE reenviar_integracao_cross (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_envio_w  		dados_envio_cross.dt_envio%type;


BEGIN

select dt_envio
into STRICT dt_envio_w
from dados_envio_cross
where nr_sequencia = nr_sequencia_p;

if (coalesce(dt_envio_w::text, '') = '') then
	CALL Gravar_Log_Cross(nr_sequencia_p, wheb_mensagem_pck.get_texto(359855), nm_usuario_p);
	CALL gravar_integracao_cross(540, 'NR_SEQUENCIA='|| nr_sequencia_p || ';CD_ESTABELECIMENTO='|| wheb_usuario_pck.get_cd_estabelecimento || ';');
else
	CALL Gravar_Log_Cross(nr_sequencia_p, wheb_mensagem_pck.get_texto(359853), nm_usuario_p);
end if;

end;
$body$
