CREATE OR REPLACE PROCEDURE reenviar_movto_rejeitado ( ds_lista_movto_p text, nr_seq_saldo_caixa_p bigint, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_caixa_od_rej_w		bigint;
nr_seq_movto_rejeitado_w	bigint;
nr_seq_movto_w			bigint;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
	a.nr_seq_movto_rejeitado 
from	movto_trans_financ a 
where	' ' || ds_lista_movto_p || ' ' like '% ' || a.nr_sequencia || ' %' 
and	(ds_lista_movto_p IS NOT NULL AND ds_lista_movto_p::text <> '') 
and	coalesce(a.nr_seq_movto_transf::text, '') = '';
				

BEGIN 
if (ds_lista_movto_p IS NOT NULL AND ds_lista_movto_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_movto_w, 
		nr_seq_movto_rejeitado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (nr_seq_movto_rejeitado_w IS NOT NULL AND nr_seq_movto_rejeitado_w::text <> '') then 
			select	max(nr_seq_caixa_od_rej) 
			into STRICT	nr_seq_caixa_od_rej_w 
			from	movto_trans_financ a 
			where	a.nr_sequencia	= nr_seq_movto_rejeitado_w;
		 
			if (nr_seq_caixa_od_rej_w IS NOT NULL AND nr_seq_caixa_od_rej_w::text <> '') then 
				CALL gerar_transferencia_caixa(nr_seq_saldo_caixa_p, 
							nr_seq_caixa_od_rej_w, 
							nr_seq_movto_w || ' ', 
							null, 
							nm_usuario_p, 
							clock_timestamp(), 
							ds_historico_p, 
							null);
			end if;
		end if;
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
