SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reenvia_cotacao_fila_transm ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_documento_externo_w		varchar(100);
nr_sequencia_w			bigint;


BEGIN

update	cot_compra
set	ie_status_envio	= 'F'
where	nr_cot_compra	= nr_cot_compra_p;

insert into cot_compra_log_integracao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_cot_compra,
	ie_status_envio,
	ie_tipo_integracao,
	ds_log)
values (	nextval('cot_compra_log_integracao_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_cot_compra_p,
	'F',
	'C',
	wheb_mensagem_pck.get_texto(306878));

select	nr_documento_externo
into STRICT	nr_documento_externo_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (nr_documento_externo_w IS NOT NULL AND nr_documento_externo_w::text <> '') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	cot_compra_fila_transm
	where	nr_cot_compra = nr_cot_compra_p
	and	ie_evento = 'R';

	if (nr_sequencia_w > 0) then
		update	cot_compra_fila_transm
		set	ie_evento = 'EA'
		where	nr_sequencia = nr_sequencia_w;
	end if;
else
	update	cot_compra_fila_transm
	set	ie_evento = 'EN'
	where	nr_cot_compra = nr_cot_compra_p
	and	ie_evento = 'R';
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
