CREATE OR REPLACE PROCEDURE regerar_cron_proj_migr_funcao ( nr_seq_cronograma_p bigint, cd_funcao_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	delete
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p;

	update	proj_cronograma
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_cronograma_p;

	CALL gerar_ativ_cron_proj_migr(nr_seq_cronograma_p, cd_funcao_p, nm_usuario_p);
	end;
end if;
commit;
end;
$body$
