CREATE OR REPLACE PROCEDURE regerar_horario_agecons ( cd_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_p bigint) AS $body$
DECLARE

 
				  
dt_inicial_w 	timestamp;
dt_final_w  	timestamp;				
dt_dia_w	timestamp;
ds_erro_w	varchar(255);
ie_feriado_w	varchar(1);
ie_sobra_horario_w varchar(1);
dt_Atual_w     timestamp;
ie_param_w	  varchar(1);
	

BEGIN 
select 	coalesce(max(ie_forma_excluir_consulta),'N') 
into STRICT 	ie_param_w 
from 	parametro_agenda 
where  cd_estabelecimento = cd_estabelecimento_p;
 
if ((coalesce(cd_agenda_p,0) > 0) and (ie_param_w = 'S')) then 
	select 	max(ie_feriado), 
		max(ie_gerar_sobra_horario) 
	into STRICT 	ie_feriado_w, 
		ie_sobra_horario_w 
	from 	agenda 
	where cd_agenda = cd_agenda_p;
 
 
	select	max(PKG_DATE_UTILS.get_datetime(dt_inicial,hr_inicio_bloqueio)) dt_inicial, 
		max(PKG_DATE_UTILS.get_datetime(dt_final,hr_final_bloqueio))  dt_final 
	into STRICT	dt_inicial_w, 
		dt_final_w 
	from	agenda_bloqueio 
	where	cd_agenda = cd_agenda_p 
	and 	(dt_inicial IS NOT NULL AND dt_inicial::text <> '') 
	and 	coalesce(dt_final,clock_timestamp()) >=clock_timestamp() 
	and 	nr_sequencia = nr_seq_p 
	order by Dt_inicial;
	 
	dt_Atual_w	:= dt_inicial_w;
	 
	while(dt_Atual_w <= dt_final_w) loop 
		begin 
		ds_erro_w := Horario_Livre_Consulta(cd_estabelecimento_p, cd_agenda_p, ie_feriado_w, dt_Atual_w, nm_usuario_p, 'S', ie_sobra_horario_w, 'N', 0, ds_erro_w);
		dt_Atual_w := dt_Atual_w + 1;
		end;
	end loop;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
