SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE regerar_mensagem_xml_integ ( nr_seq_agendamento_integ_p bigint) AS $body$
DECLARE


nr_seq_log_w 		bigint;
nr_seq_proj_xml_w  	bigint;
ds_parametros_w		varchar(4000);


BEGIN

if (nr_seq_agendamento_integ_p	 > 0 ) then

	select	a.nr_seq_log,
			a.ds_parametros
	into STRICT	nr_seq_log_w,
			ds_parametros_w
	from	agendamento_integracao a
	where	nr_sequencia = nr_seq_agendamento_integ_p;


	delete
	from 	log_integracao_xml
	where 	nr_seq_log = nr_seq_log_w;


	select	i.nr_seq_proj_xml
	into STRICT	nr_seq_proj_xml_w
	from	log_integracao l,
			informacao_integracao i
	where	l.nr_sequencia = nr_seq_log_w
	and		l.nr_seq_informacao = i.nr_sequencia;


	CALL wheb_exportar_xml_pck.exportar(nr_seq_proj_xml_w,nr_seq_log_w,'INTEGRACAO',ds_parametros_w);


end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
