CREATE OR REPLACE PROCEDURE registar_apagar_buscar_pac (nr_cirurgia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*ie_opcao_p:
	'R' = Registrar
	'A = Apagar'*/
BEGIN
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	if (ie_opcao_p = 'R') then
		update	agenda_paciente
		set	nm_usuario		=	nm_usuario_p,
			nm_buscar_paciente	=	nm_usuario_p,
			dt_atualizacao		=	clock_timestamp(),
			dt_buscar_paciente	=	clock_timestamp()
		where	nr_cirurgia		=	nr_cirurgia_p;

	elsif (ie_opcao_p = 'A') then
		update	agenda_paciente
		set	nm_usuario		=	nm_usuario_p,
			nm_buscar_paciente	 = NULL,
			dt_atualizacao		=	clock_timestamp(),
			dt_buscar_paciente	 = NULL
		where	nr_cirurgia		=	nr_cirurgia_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
