CREATE OR REPLACE PROCEDURE register_reason_history ( nr_encounter_p bigint, nm_user_p text) AS $body$
BEGIN
if (nr_encounter_p IS NOT NULL AND nr_encounter_p::text <> '') and (nm_user_p IS NOT NULL AND nm_user_p::text <> '') then
	begin

	delete
	from	privacy_reason_history
	where	nm_usuario_nrec = nm_user_p
	and		nr_atendimento = nr_encounter_p;

	commit;

	insert into
	privacy_reason_history(nr_sequencia, dt_atualizacao_nrec, nm_usuario_nrec, nr_atendimento)
	values ( nextval('privacy_reason_history_seq'), clock_timestamp(), nm_user_p, nr_encounter_p);

	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
