SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE registrar_dias_item_rep ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_tipo_item_p text, ds_dias_p text, nm_usuario_p text) AS $body$
DECLARE


ds_dias_w		varchar(4000);
dt_dia_w		timestamp;
nr_nova_seq_w	bigint;
nr_posicao_w	bigint;


BEGIN

if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_tipo_item_p = 'SOL') then
		delete from rep_dias_solucao
		where	nr_prescricao = nr_prescricao_p
		and		nr_seq_solucao = nr_sequencia_p;
	end if;
	commit;

	ds_dias_w		:= ds_dias_p;
	nr_posicao_w	:= position(',' in ds_dias_w);
	while(nr_posicao_w > 0) loop
		begin
		dt_dia_w	:= to_date(substr(ds_dias_w,1,nr_posicao_w-1),'dd/mm/yyyy hh24:mi:ss');

		select	nextval('rep_dias_solucao_seq')
		into STRICT	nr_nova_seq_w
		;

		insert into rep_dias_solucao(
				nr_sequencia,
				nr_prescricao,
				nr_seq_solucao,
				dt_solucao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec
		) values (
				nr_nova_seq_w,
				nr_prescricao_p,
				nr_sequencia_p,
				dt_dia_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p
		);

		ds_dias_w		:= substr(ds_dias_w,nr_posicao_w+1,length(ds_dias_w));
		nr_posicao_w	:= position(',' in ds_dias_w);
		end;
	end loop;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
