CREATE OR REPLACE PROCEDURE registrar_entrada_saida_pf ( cd_pessoa_fisica_p text, cd_tipo_controle_p bigint, ie_entrada_saida_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;


BEGIN

if (ie_entrada_saida_p = 'E') then

	insert into controle_pessoa_comp(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_tipo_controle,
		cd_pessoa_fisica,
		dt_entrada)
	values (nextval('controle_pessoa_comp_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_tipo_controle_p,
		cd_pessoa_fisica_p,
		clock_timestamp());

elsif (ie_entrada_saida_p = 'S') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	controle_pessoa_comp
	where	cd_tipo_controle = cd_tipo_controle_p
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_saida::text, '') = '';

	if (nr_sequencia_w > 0) then

		update	controle_pessoa_comp
		set	dt_saida = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
