CREATE OR REPLACE PROCEDURE registrar_farm_manip_prod ( nr_sequencia_p bigint, nm_usuario_farmaceutico_p text, nm_usuario_manipulador_p text) AS $body$
BEGIN
 
if (nr_sequencia_p > 0) then 
	update	lp_individual 
	set	cd_manipulador	=	obter_dados_usuario_opcao(nm_usuario_manipulador_p,'C'), 
		cd_farmaceutico	=	obter_dados_usuario_opcao(nm_usuario_farmaceutico_p,'C') 
	where	nr_sequencia	=	nr_sequencia_p;
 
	commit;
end if;
 
end;
