CREATE OR REPLACE PROCEDURE registrar_inconsistencia_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_inconsistencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_prescricao_p > 0) and (nr_sequencia_p > 0)  then
	if (ie_opcao_p = 'R') then
		update	prescr_material
		set		nr_seq_inconsistencia	=	nr_seq_inconsistencia_p
		where	nr_prescricao		=	nr_prescricao_p
		and    ((nr_sequencia		=	nr_sequencia_p) OR (nr_seq_kit =   nr_sequencia_p) or (nr_sequencia_diluicao = nr_sequencia_p));
	else
		update	prescr_material
		set		nr_seq_inconsistencia	 = NULL
		where	nr_prescricao		=	nr_prescricao_p
		AND    ((nr_sequencia		=	nr_sequencia_p) OR (nr_seq_kit =   nr_sequencia_p) or (nr_sequencia_diluicao = nr_sequencia_p));
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
