CREATE OR REPLACE PROCEDURE registrar_incons_farm_soluc ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_seq_inconsistencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

