CREATE OR REPLACE PROCEDURE registrar_itens_controle ( nr_sequencia_p bigint, cd_material_p bigint, nr_seq_item_cme_p bigint, nr_cirurgia_p bigint, qt_item_p bigint, ie_acao_p bigint, qt_peso_p bigint, nm_usuario_p text, ds_justificativa_p text, nr_seq_controle_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	nextval('cirurgia_item_controle_seq')
into STRICT	nr_sequencia_w
;

insert into cirurgia_item_controle(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_cirurgia,
	nr_seq_item_controle,
	ie_acao,
	qt_item,
	qt_peso,
	cd_material,
	nr_seq_item_cme,
	ds_justificativa,
	ie_situacao)
values (
	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_cirurgia_p,
	nr_sequencia_p,
	ie_acao_p,
	qt_item_p,
	qt_peso_p,
	CASE WHEN cd_material_p=0 THEN null  ELSE cd_material_p END ,
	CASE WHEN nr_seq_item_cme_p=0 THEN null  ELSE nr_seq_item_cme_p END ,
	ds_justificativa_p,
	'A');

commit;

nr_seq_controle_p	:= nr_sequencia_w;

end;
