CREATE OR REPLACE PROCEDURE registrar_log_aplicacao_swing ( nm_projeto_p text, dt_ult_commit_p timestamp, nm_usuario_commit_p text, ds_revisao_p text, ds_versao_java_p text, dt_versao_java_p timestamp, nm_usuario_p text) AS $body$
DECLARE


