BEGIN

select	count(*)
into STRICT	qt_registros_w
from	aplicacao_swing_log
where	nm_projeto = nm_projeto_p;

if ( qt_registros_w = 0) then

	insert into aplicacao_swing_log(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nm_projeto,
		dt_ult_commit,
		nm_usuario_commit,
		ds_revisao,
		ds_versao_java,
		dt_versao_java
	) values (
		nextval('aplicacao_swing_log_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nm_projeto_p,
		dt_ult_commit_p,
		nm_usuario_commit_p,
		ds_revisao_p,
		ds_versao_java_p,
		dt_versao_java_p
	);

	commit;

else

	update 	aplicacao_swing_log
	set	nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		dt_ult_commit = dt_ult_commit_p,
		nm_usuario_commit = nm_usuario_commit_p,
		ds_revisao = ds_revisao_p,
		ds_versao_java = ds_versao_java_p,
		dt_versao_java = dt_versao_java_p
	where	nm_projeto = nm_projeto_p;

	commit;

end if;

