SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE registrar_receb_inven ( nr_seq_inventario_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pendente_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_pendente_w
from	rop_inv_reposicao_item
where	nr_seq_inventario = nr_seq_inventario_p
and	coalesce(dt_recebimento::text, '') = '';


update  rop_inv_reposicao
set	dt_recebimento = clock_timestamp(),
	nm_usuario_receb = nm_usuario_p
where	nr_sequencia = nr_seq_inventario_p;

if (qt_pendente_w > 0) then
	update	rop_inv_reposicao_item
	set	dt_recebimento = clock_timestamp(),
		nm_usuario_receb = nm_usuario_p
	where	nr_seq_inventario = nr_seq_inventario_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
