SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE registrar_saida_setor_cirur ( nr_cirurgia_p bigint, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE



dt_entrada_unidade_w	timestamp;
nr_atendimento_w	bigint;
dt_interrupcao_w	timestamp;
ie_dt_interrupcao_w	varchar(1);

/*
Ações
I - interromper a cirurgia
D - desfazer a interrupção da cirurgia
*/
BEGIN

ie_dt_interrupcao_w := obter_param_usuario(900, 520, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_dt_interrupcao_w);

if (nr_cirurgia_p > 0) then

	select	dt_entrada_unidade,
		nr_atendimento,
		dt_interrupcao
	into STRICT	dt_entrada_unidade_w,
		nr_atendimento_w,
		dt_interrupcao_w
	from	cirurgia
	where	nr_cirurgia	=	nr_cirurgia_p;

	if (dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (ie_acao_p = 'I') then
		update	atend_paciente_unidade
		set	dt_saida_unidade	= CASE WHEN ie_dt_interrupcao_w='S' THEN dt_interrupcao_w  ELSE clock_timestamp() END ,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where 	nr_atendimento		= nr_atendimento_w
		and	dt_entrada_unidade	= dt_entrada_unidade_w;
		commit;
	else if (dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (ie_acao_p = 'D') then
			update	atend_paciente_unidade
			set	dt_saida_unidade	 = NULL,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where 	nr_atendimento		= nr_atendimento_w
			and	dt_entrada_unidade	= dt_entrada_unidade_w;
			commit;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
