CREATE OR REPLACE PROCEDURE registrar_sepsis_palm ( nr_atendimento_p bigint, nr_seq_sinal_vital_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
 
dt_liberacao_w			timestamp	:= null;
qt_reg_w				bigint;
ie_sepse_lib_sv_w		varchar(1);
sql_errm_w				varchar(2000);
cd_estabelecimento_w	smallint;
cd_pessoa_fisica_w		varchar(10);


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(nr_seq_sinal_vital_p,0) > 0) then 
	 
	Select max(dt_liberacao) 
	into STRICT	dt_liberacao_w 
	from	atendimento_sinal_vital 
	where	nr_sequencia = nr_seq_sinal_vital_p;
	 
	Select 	obter_pf_usuario(nm_usuario_p,'C') 
	into STRICT	cd_pessoa_fisica_w 
	;
		 
	 
	If (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then 
			 
		cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
		 
 
		select max(coalesce(ie_sepse_lib_sv,'N')) 
		into STRICT	ie_sepse_lib_sv_w 
		from 	parametro_medico 
		where  cd_estabelecimento = cd_estabelecimento_w;
		 
		if (ie_sepse_lib_sv_w = 'S') then 
			 
			begin 
			 
			CALL gerar_escala_sepse(nr_atendimento_p,cd_pessoa_fisica_w,nr_seq_sinal_vital_p,nm_usuario_p);		
			exception 
				when others then 
					sql_errm_w := sqlerrm;		
			end;
		 
		end if;
		 
	end if;
	 
end if;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
