SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE registrar_temperatura_dieta ( ie_tipo_item_p text, ds_observacao_p text, qt_temperatura_p bigint, nr_prescricao_p bigint, nr_atendimento_p bigint, cd_item_p text, nr_seq_solucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_alteracao_w	bigint;
ds_obs_w		varchar(2000);
			

BEGIN

if (ie_tipo_item_p	= 'D') then

	SELECT	nextval('prescr_mat_alteracao_seq')
	INTO STRICT	nr_seq_alteracao_w
	;

	INSERT	INTO	prescr_mat_alteracao(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			dt_alteracao,
			cd_pessoa_fisica,
			ie_alteracao,
			ds_observacao,
			ie_tipo_item,
			nr_atendimento,
			cd_item)
		VALUES (nr_seq_alteracao_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			clock_timestamp(),
			obter_dados_usuario_opcao(nm_usuario_p,'C'),
			37,
			substr(obter_desc_expressao(299207)|| qt_temperatura_p || obter_desc_expressao(688837) || ' ' || chr(13) || ds_observacao_p,1,2000),
			ie_tipo_item_p,
			nr_atendimento_p,
			cd_item_p);
elsif (ie_tipo_item_p	= 'S') then
		SELECT	nextval('prescr_mat_alteracao_seq')
		INTO STRICT	nr_seq_alteracao_w
		;

		INSERT	INTO	prescr_mat_alteracao(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			dt_alteracao,
			cd_pessoa_fisica,
			ie_alteracao,
			ds_observacao,
			ie_tipo_item,
			nr_atendimento,
			cd_item)
		VALUES (nr_seq_alteracao_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			clock_timestamp(),
			obter_dados_usuario_opcao(nm_usuario_p,'C'),
			37,
			substr(obter_desc_expressao(299207) || qt_temperatura_p || obter_desc_expressao(688837) || ' ' || chr(13) || ds_observacao_p,1,2000),
			ie_tipo_item_p,
			nr_atendimento_p,
			cd_item_p);
else
	/* obter sequencia */

	select	nextval('prescr_solucao_evento_seq')
	into STRICT	nr_seq_alteracao_w
	;
	
	ds_obs_w	:= substr(obter_desc_expressao(299207) || to_char(qt_temperatura_p) || obter_desc_expressao(688837) || ' ' || ds_observacao_p,1,2000);
	
	/* gerar evento */

	insert into prescr_solucao_evento(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_material,
		cd_pessoa_fisica,
		ie_alteracao,
		dt_alteracao,
		ie_evento_valido,
		ds_observacao,
		ie_tipo_solucao)
	values (
		nr_seq_alteracao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_prescricao_p,
		nr_seq_solucao_p,		
		obter_dados_usuario_opcao(nm_usuario_p, 'C'),
		31,
		clock_timestamp(),
		'S',
		ds_obs_w,
		2);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
