SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE registra_fim_adm_qt ( nr_seq_atendimento_p bigint, nr_seq_p bigint, ie_tipo_seq_p text, cd_profissional_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w		bigint;
cd_profissional_w       varchar(10);
					
C01 CURSOR FOR
	SELECT	nr_seq_material
	from 	paciente_atend_medic
	where	nr_seq_atendimento	=	nr_seq_atendimento_p
	and	nr_agrupamento		=	nr_seq_p;
	
C02 CURSOR FOR
	SELECT	nr_seq_material
	from 	paciente_atend_medic
	where	nr_seq_atendimento	=	nr_seq_atendimento_p
	and	nr_seq_solucao		=	nr_seq_p;
	

BEGIN

if (coalesce(cd_profissional_p::text, '') = '') then
	cd_profissional_w := substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10);
else
	cd_profissional_w := cd_profissional_w;
end if;

if (ie_tipo_seq_p = 'A') then  -- Agrupador
	open C01;
	loop
	fetch C01 into	
		nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
			
			update	paciente_atend_medic_adm
			set	dt_fim_administracao 	= clock_timestamp(),
				cd_profissional_termino = cd_profissional_w,
				nm_usuario 		= nm_usuario_p
			where	nr_seq_atendimento 	= nr_seq_atendimento_p
			and	nr_seq_material 	= nr_seq_material_w;

            update	paciente_atend_medic
            set		ie_administracao	=	'A'
            where	nr_seq_atendimento	=	nr_seq_atendimento_p
            and		nr_agrupamento		=	nr_seq_p;
			
		end if;
		end;
	end loop;
	close C01;

elsif (ie_tipo_seq_p = 'S') then  -- Solucao
	open C02;
	loop
	fetch C02 into	
		nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		
		if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
			
			update	paciente_atend_medic_adm
			set	dt_fim_administracao 	= clock_timestamp(),
				cd_profissional_termino = cd_profissional_w,
				nm_usuario 		= nm_usuario_p
			where	nr_seq_atendimento 	= nr_seq_atendimento_p
			and	nr_seq_material 	= nr_seq_material_w
			and	coalesce(dt_fim_administracao::text, '') = '';

            update	paciente_atend_medic
            set		ie_administracao	=	'A'
            where	nr_seq_atendimento	=	nr_seq_atendimento_p
            and		(nr_seq_solucao IS NOT NULL AND nr_seq_solucao::text <> '');

		end if;
		end;
	end loop;
	close C02;

elsif (ie_tipo_seq_p = 'M') then  -- Material
	if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nr_seq_p IS NOT NULL AND nr_seq_p::text <> '') then
		
		update	paciente_atend_medic_adm
		set	dt_fim_administracao 	= clock_timestamp(),
			cd_profissional_termino = cd_profissional_w,
			nm_usuario 		= nm_usuario_p
		where	nr_seq_atendimento 	= nr_seq_atendimento_p
		and	nr_seq_material 	= nr_seq_p
		and	coalesce(dt_fim_administracao::text, '') = '';

    update  paciente_atend_medic
    set     ie_administracao	=	'A'
    where   nr_seq_atendimento	=	nr_seq_atendimento_p
    and     nr_seq_material	=	nr_seq_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
