CREATE OR REPLACE PROCEDURE regra_agenda_liber_cidade (cd_pessoa_fisica_p text, cd_agenda_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


cd_municipio_w	bigint;
qt_existe_w	bigint;
qt_agenda_w	bigint;


BEGIN

select	coalesce(max(cd_municipio_ibge),0)
into STRICT	cd_municipio_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

select	count(*)
into STRICT	qt_agenda_w
from	regra_agenda_lib_cidade
where	cd_agenda = cd_agenda_p;

if (qt_agenda_w > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	regra_agenda_lib_cidade
	where	cd_municipio_ibge = cd_municipio_w
	and	cd_agenda = cd_agenda_p;

if (qt_existe_w = 0) then

	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(277637,null);

end if;

end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
