CREATE OR REPLACE PROCEDURE regra_alterar_local_status_job (nr_atendimento_p text, ie_acao_p text, nm_usuario_p text, cd_perfil_p bigint default null, cd_estabelecimento_p bigint default null) AS $body$
DECLARE

			 
nr_seq_local_pa_w	bigint;
nr_seq_pa_status_w	bigint;
cd_perfil_w		integer;
cd_estabelecimento_w	smallint;
qt_registros_w		integer;
ds_erro_w		varchar(255);

 
 

BEGIN 
 
cd_perfil_w := coalesce(cd_perfil_p,obter_perfil_ativo);
cd_estabelecimento_w := coalesce(cd_estabelecimento_p,obter_estabelecimento_ativo);
 
SELECT	count(*) 
INTO STRICT	qt_registros_w 
FROM	REGRA_ATEND_LOCAL_STATUS 
WHERE	ie_situacao = 'A' 
and	ie_acao = ie_acao_p 
AND	cd_estabelecimento = cd_estabelecimento_w 
AND	cd_perfil = cd_perfil_w;
 
if (qt_registros_w > 0) then 
	begin 
	SELECT	max(nr_seq_local_pa), 
		max(nr_seq_pa_status) 
	INTO STRICT	nr_seq_local_pa_w, 
		nr_seq_pa_status_w 
	FROM	REGRA_ATEND_LOCAL_STATUS 
	WHERE	ie_situacao = 'A' 
	and	ie_acao = ie_acao_p 
	AND	cd_estabelecimento = cd_estabelecimento_w 
	AND	cd_perfil = cd_perfil_w;
 
	if (nr_seq_local_pa_w IS NOT NULL AND nr_seq_local_pa_w::text <> '') and (nr_seq_pa_status_w IS NOT NULL AND nr_seq_pa_status_w::text <> '') and (nr_atendimento_p > 0) then 
		begin 
		 
		 
		ds_erro_w := troca_local_paciente_pa(nr_seq_local_pa_w, nr_atendimento_p, '0', nm_usuario_p, obter_desc_expressao(615512), ds_erro_w);
			 
		if (coalesce(ds_erro_w::text, '') = '') then 
			CALL alterar_status_pa_atend(nr_seq_pa_status_w, nr_atendimento_p);
			 
		end if;
		commit;
		exception 
			when others then 
			null;
		 
		end;
	end if;
	end;
end if;
 
 
end;
