CREATE OR REPLACE PROCEDURE regra_alterar_status_farmacia ( nr_prescricao_p bigint, ie_momento_p text, nm_usuario_p text) AS $body$
DECLARE


ie_possui_w		varchar(1) := 'N';
nr_seq_status_w		bigint;


BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_w
from   	regra_status_farmacia
where  	ie_momento = ie_momento_p;


if (ie_possui_w = 'S') then

	select max(NR_SEQ_STATUS)
	into STRICT   nr_seq_status_w
	from   regra_status_farmacia
	where  ie_momento = ie_momento_p;

	if (nr_seq_status_w > 0) then
		update prescr_medica
		set    nr_seq_status_farm = nr_seq_status_w
		where  nr_prescricao	  = nr_prescricao_p;

	commit;
	end if;
end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
