CREATE OR REPLACE PROCEDURE reg_activate_program_cts ( nr_seq_reg_program_p bigint, nr_seq_caso_teste_p bigint, ds_reason_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

  if (nr_seq_reg_program_p IS NOT NULL AND nr_seq_reg_program_p::text <> '' AND nr_seq_caso_teste_p IS NOT NULL AND nr_seq_caso_teste_p::text <> '') then

    if (ie_opcao_p = 'URS') then
    
      update reg_program_urs_cts
      
         set ie_situacao = 'A',
             
             ds_justificativa_inativacao  = NULL,
             
             ds_justificativa_ativacao = ds_reason_p,
             
             nm_usuario = nm_usuario_p,
             
             dt_atualizacao = clock_timestamp()
      
       where nr_seq_reg_program = nr_seq_reg_program_p
            
         and nr_seq_caso_teste = nr_seq_caso_teste_p;

    elsif (ie_opcao_p = 'PRS') then
    
      update reg_program_prs_cts
      
         set ie_situacao = 'I',
             
             ds_justificativa_inativacao  = NULL,
             
             ds_justificativa_ativacao = ds_reason_p,
             
             nm_usuario = nm_usuario_p,
             
             dt_atualizacao = clock_timestamp()
      
       where nr_seq_reg_program = nr_seq_reg_program_p
            
         and nr_seq_caso_teste = nr_seq_caso_teste_p;

    end if;

    commit;

  end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
