CREATE OR REPLACE PROCEDURE reg_add_document_revision ( nr_seq_documento_p reg_documento.nr_sequencia%TYPE, nm_usuario_p reg_revisao_documento.nm_usuario%TYPE ) AS $body$
DECLARE

	nr_seq_versao_w	reg_versao_documento.nr_sequencia%TYPE;
	cd_revisao_w	reg_revisao_documento.cd_revisao%TYPE;

BEGIN
	SELECT	nr_sequencia
	INTO STRICT	nr_seq_versao_w
	FROM	reg_versao_documento
	WHERE	nr_seq_documento = nr_seq_documento_p;

	SELECT	CHR(ASCII(MAX(cd_revisao))) +1
	INTO STRICT	cd_revisao_w
	FROM	reg_revisao_documento
	WHERE	nr_seq_documento = nr_seq_documento_p;

	INSERT INTO reg_revisao_documento(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_documento, cd_revisao, nr_seq_versao)
	VALUES (nextval('reg_revisao_documento_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, nr_seq_documento_p, cd_revisao_w, nr_seq_versao_w);

	COMMIT;
END;
$body$
