SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reg_add_val_pend_itens ( nr_seq_pendency bigint, nr_seq_ct_p bigint, nm_usuario_p text, ie_duplicate_all_p text) AS $body$
DECLARE


ds_passo_w	varchar(4000);
ds_resultado_w	varchar(4000);
qt_count_w	bigint;
nr_seq_item_w	bigint;

nr_seq_cycle_w	reg_validation_pend_item.nr_seq_cycle%type;

c01 CURSOR FOR
	SELECT	rat.ds_passo,
		rat.ds_resultado,
		rat.nr_sequencia
	from	reg_acao_teste rat,
		reg_caso_teste rct
	where	rat.nr_seq_caso_teste = nr_seq_ct_p
	and	coalesce(rat.ie_situacao, 'A') = 'A'
	and	rct.nr_sequencia = rat.nr_seq_caso_teste
	and (coalesce(rct.ie_situacao,'A') <> 'I')
	order by ds_passo;
	
c_test_case_items CURSOR FOR
	SELECT	*
	from (
		SELECT	ac.nr_sequencia nr_seq_pend_item,
			ev.ie_result,
			rank() over (partition by ac.nr_sequencia order by ev.nr_seq_cycle desc) rnk
		from	reg_validation_pend_item ev,
			reg_acao_teste ac
		where	ev.nr_seq_pend_tc = nr_seq_pendency
		and	ev.nr_seq_pend_item = ac.nr_sequencia
		order by ev.nr_seq_cycle) alias2
	where	rnk = 1;

BEGIN
	select	count(nr_sequencia)
	into STRICT	qt_count_w
	from	reg_validation_pend_item
	where	nr_seq_pend_tc = nr_seq_pendency;
	
	if (qt_count_w > 0) then
		
		select	count(nr_sequencia)
		into STRICT	qt_count_w
		from	reg_validation_pend_item
		where	nr_seq_pend_tc = nr_seq_pendency
		and	coalesce(ie_result::text, '') = '';
		
		if (qt_count_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(836886);
		end if;
		
		select	max(nr_seq_cycle)
		into STRICT	nr_seq_cycle_w
		from	reg_validation_pend_item
		where	nr_seq_pend_tc = nr_seq_pendency;
		
		for	tc_item in c_test_case_items
		loop
			if	ie_duplicate_all_p = 'S' or tc_item.ie_result <> 'P' then
			
				SELECT	max(ac.ds_resultado),
					max(ac.ds_passo)
				into STRICT	ds_resultado_w,
					ds_passo_w
				FROM	reg_acao_teste ac
				WHERE	ac.nr_sequencia = tc_item.nr_seq_pend_item;
				
			
				insert into reg_validation_pend_item(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_pend_tc,
					nr_seq_cycle,
					nr_seq_pend_item,
					nm_user_execution,
					dt_execution,
					DS_ITEM_DESCRIPTION,
					DS_ITEM_RESULT)
				values (	nextval('reg_validation_pend_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_pendency,
					nr_seq_cycle_w+1,
					tc_item.nr_seq_pend_item,
					nm_usuario_p,
					clock_timestamp(),
					ds_passo_w,
					ds_resultado_w);
			end if;
		end loop;
	else
	
		open c01;
		loop
		fetch c01 into	
			ds_passo_w,
			ds_resultado_w,
			nr_seq_item_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
				insert into reg_validation_pend_item(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_pend_tc,
					nr_seq_cycle,
					nr_seq_pend_item,
					nm_user_execution,
					dt_execution,
					DS_ITEM_DESCRIPTION,
					DS_ITEM_RESULT)
				values (	nextval('reg_validation_pend_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_pendency,
					--ds_passo_w,
					1,
					nr_seq_item_w,
					nm_usuario_p,
					clock_timestamp(),
					ds_passo_w,
					ds_resultado_w);
			end;
		end loop;
		close c01;
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE reg_add_val_pend_itens ( nr_seq_pendency bigint, nr_seq_ct_p bigint, nm_usuario_p text, ie_duplicate_all_p text) FROM PUBLIC;

