CREATE OR REPLACE PROCEDURE reg_approve_revision ( nr_seq_revision_doc_p reg_revisao_documento.nr_sequencia%TYPE, nm_usuario_p reg_aprovacao_documento.nm_usuario%TYPE ) AS $body$
DECLARE

/**
 * Alterna entre aprovar e desfazer a aprovação da revisão do docucmento.
 *	Se o documento passado por parâmetro já estiver aprovado, a aprovação é desfeita.
 *	Caso não estiver aprovado, é realizada a aprovação do mesmo.
 */
	nr_seq_approval_w	reg_aprovacao_documento.nr_sequencia%TYPE;

BEGIN
	SELECT	MAX(nr_sequencia)
	INTO STRICT	nr_seq_approval_w
	FROM	reg_aprovacao_documento
	WHERE	nr_seq_revisao = nr_seq_revision_doc_p;

	IF	(nr_seq_approval_w IS NOT NULL AND nr_seq_approval_w::text <> '') THEN
		DELETE FROM reg_aprovacao_documento
		WHERE	nr_sequencia = nr_seq_approval_w;
	ELSE
		INSERT INTO reg_aprovacao_documento(nr_sequencia, nr_seq_revisao, nm_usuario, dt_atualizacao)
		VALUES (nextval('reg_aprovacao_documento_seq'), nr_seq_revision_doc_p, nm_usuario_p, clock_timestamp());
	END IF;

	COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
