CREATE OR REPLACE PROCEDURE reg_ativar_caso_teste ( nr_seq_caso_teste_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	reg_acao_teste
	where	ie_situacao <> 'A'
	and	nr_seq_caso_teste = nr_seq_caso_teste_p;

BEGIN

update	reg_caso_teste
set	ie_situacao = 'A',
	dt_inativacao  = NULL,
	nm_usuario_inativacao  = NULL,
	ds_motivo_inativacao  = NULL
where	nr_sequencia = nr_seq_caso_teste_p;

commit;

for r_c01 in c01 loop
	CALL reg_ativar_acao_teste(r_c01.nr_sequencia, nm_usuario_p);
end loop;

end;
$body$
